    private void btnAgregarTrienioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarTrienioActionPerformed
        plazas.put(spnNumPlaza.getValue().toString(), txtDesPlaza.getText());
        Login.AgregarATabla(Login.tblPlaza, spnNumPlaza.getValue().toString(), txtDesPlaza.getText());
        
        this.setVisible(false);     
    }//GEN-LAST:event_btnAgregarTrienioActionPerformed

